mapper.select:
select id
from tbl_user
where name=#{name}

select name
from tbl_user
where id=#{id}

select count(*)
from tbl_user


        name like '%' #{name} '%'


        and lockState like '%' #{lockState2Char} '%'


        and expireTime > #{startTime}


        and expireTime < #{endTime}

select *
from tbl_user


        name like '%' #{name} '%'


        and lockState like '%' #{lockState2Char} '%'


        and expireTime > #{startTime}


        and expireTime < #{endTime}


limit #{skipCount},#{pageSize}

mapper.update:
update tbl_user set
                    loginPwd = #{loginPwd},
                    editTime = #{editTime},
                    editBy = #{editBy}
where id=#{id}

mapper.delete:
delete from tbl_user
where id in

    #{id}
